mapper.select:
select role_id as id, role_name as role_name, permissions as permissions, create_name as createName, create_time as createTime, update_name as updateName, update_time as updateTime from sys_role where 1=1
and role_name like "%"#{roleName}"%"
limit #{lowerLimit},#{upperLimit}

mapper.update:
update sys_role set 
role_name=#{role_name},
permissions=#{permissions},
update_name=#{update_name}
where role_id=#{id}
