mapper.update:
update ws_spec 

	spec_name=#{specname},
	update_name=#{updateName},
	update_time=#{updateTime}

where spec_id=#{id}

mapper.select:
select ws.spec_id as id, ws.spec_name as specname, ws.spec_remark as remark, ws.create_name as createName, ws.update_time as updateTime from ws_spec as ws where 1=1
and ws.spec_name like CONCAT('%','${specname}','%')
limit #{lowerLimit},#{upperLimit}
