mapper.update:
update ws_supplier
supp_company=#{suppcompany}, supp_name=#{suppname}, supp_tel=#{supptel}, supp_remark=#{remark}, update_name=#{updateName}, update_time=#{updateTime}
where supp_id=#{id}

mapper.select:
select ws.supp_id as id, ws.supp_company as suppcompany, ws.supp_name as suppname, ws.supp_tel as supptel, ws.supp_remark as remark, ws.create_name as createName, ws.create_time as createTime, ws.update_time as updateTime from ws_supplier as ws where 1=1
and ws.supp_company like CONCAT('%','${suppcompany}','%')
limit #{lowerLimit},#{upperLimit}
